
public class Lab2_1 {

	public static void main(String[] args) {
		String strVar1 = "9.81", strVar2 = "15.5";
		// TODO Auto-generated method stub
		
		// Convert String to float
		float var1 = Float.parseFloat(strVar1);
		float var2 = Float.parseFloat(strVar2);
		
		// Display sum and multiplication
		System.out.println("The sum of "+strVar1+" and "+strVar2+" is "+ (var1+var2) + ". ");
		System.out.println("The multiplication of "+strVar1+" and "+strVar2+" is "+ (var1*var2) + ". ");
	
	}

}
